(window.webpackJsonp=window.webpackJsonp||[]).push([[40],{395:function(t,a,v){"use strict";v.r(a);var _=v(42),s=Object(_.a)({},(function(){var t=this,a=t.$createElement,v=t._self._c||a;return v("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[v("h1",{attrs:{id:"成就-优秀管理者成就自己-卓越管理者成就他人"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#成就-优秀管理者成就自己-卓越管理者成就他人"}},[t._v("#")]),t._v(" 成就 - 优秀管理者成就自己，卓越管理者成就他人")]),t._v(" "),v("h2",{attrs:{id:"摘要"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#摘要"}},[t._v("#")]),t._v(" 摘要")]),t._v(" "),v("p",[t._v("斯科特说：“第一次见面时，我们聊了聊商业和战略，但再聊的时候，我们没聊战略，而是聊了聊领导力和人的管理。一个人身上的特质基本上是固定的某一种，但比尔身上却融合了丰富的特质。他认为每个人都有各异的经历和背景。在应对增长挑战和领导力挑战时，他表现得是那么细致入微、与众不同。我当时想找到一种我不太擅长的方式让公司的员工得到成长，而比尔对此非常擅长。”")]),t._v(" "),v("p",[t._v("用现在的话说，要想在团队中建立“心理安全”，建立信任是其中的关键一步。根据康奈尔大学1999年的一项研究，团队心理安全是“团队成员的一种共同信念，即团队可以承担人际风险……它还是一种团队氛围……让人们可以舒服地做自己”。这正是我们和比尔一起工作时所体验到的感觉，他能很快建立一种关系，让我们可以毫无畏惧地做自己。不出意料，当谷歌通过一次调研来确定高绩效团队背后的成功因素时，心理安全被排在了第一位。之前大家普遍认为，最好的团队是由技能互补或性格相似的人组成的，但调研结果表明这种观点并不正确；最好的团队是心理安全感最强的团队，而心理安全感源于信任。")]),t._v(" "),v("h3",{attrs:{id:"产品和团队"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#产品和团队"}},[t._v("#")]),t._v(" 产品和团队")]),t._v(" "),v("p",[t._v("坎贝尔认为企业的使命是将你对未来的远见融入产品，并在现实中实现（The purpose of a company is to bring a product vision to life）。\n我非常喜欢并认同这句话。无论是一瓶饮料、一杯咖啡、一个搜索框、一部手机、一个App、一栋房子，企业真正的使命应该是找到自己独一无二的价值，并把这些注入在产品中，带到这个世界。")]),t._v(" "),v("p",[t._v("坎贝尔有两点做的特别好：\n第一，在坎贝尔指导过的企业中，当问题发生的时候，他的第一反应总是去关注团队，而不是解决问题本身；\n第二，坎贝尔说的团队指的是「精诚合作」的团队，是为了共同目标而一起努力的团队，是 「1 + 1 > 2」的团队。")]),t._v(" "),v("p",[t._v("在打造团队这件事上，他把自己的所有精力放在以下两件事情：")]),t._v(" "),v("ol",[v("li",[t._v("人是所有事情里最重要的一部分，我们必须努力让他们变得更好；")]),t._v(" "),v("li",[t._v("建立人与人之间的信任，打造具有「心理安全感」的团队。")])]),t._v(" "),v("h3",{attrs:{id:"信任"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#信任"}},[t._v("#")]),t._v(" 信任")]),t._v(" "),v("blockquote",[v("p",[t._v("信任并不意味着总是要认同别人，事实上，信任能让人更容易地不认同某人的观点。")])]),t._v(" "),v("p",[t._v("坎贝尔认为，「事务冲突是健康的，它对做出最好的决策关系重大，但它也与人际冲突高度相关，而后者会导致决策失准和士气下降」。该怎么办呢？要建立信任。相互信任的团队之间仍然会存在分歧，但当分歧出现的时候，彼此情感上的怨恨会少很多。")]),t._v(" "),v("p",[t._v("坎贝尔认为教练（管理者）需要在人与人之间的裂隙变得更深、演化成永久的分歧之前就发现它们，并通过填补"),v("strong",[t._v("信息差和纠正沟通")]),t._v("不畅予以解决。")]),t._v(" "),v("p",[t._v("他花了大量的时间，通过倾听、观察，填补人与人之间沟通和理解的鸿沟。无论是开会前单独和大家交谈、了解大家的想法，会中避免消极和冲突情绪让大家专注在讨论上，还是会后和提案失败的同事单独沟通，他努力让团队的所有人相信大家都想着「公司第一」，而不是「自我第一」。")]),t._v(" "),v("h3",{attrs:{id:"真诚"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#真诚"}},[t._v("#")]),t._v(" 真诚")]),t._v(" "),v("p",[t._v("坎贝尔式的倾听，是因为他真诚的希望了解对方；坎贝尔对团队和人的关注，是因为他真诚的希望这些人变的更好；坎贝尔的批评和粗话让大家乐于接受，因为他们感受到这些话背后真诚的关心。")]),t._v(" "),v("p",[t._v("坎贝尔最标志性的动作是拥抱。对于见过的所有人，他都给过一个拥抱。而且这种拥抱不是那种礼节性的、小心翼翼的、不走心的拥抱。坎贝尔式的拥抱是熊抱、认认真真、结结实实的熊抱。")]),t._v(" "),v("p",[t._v("是的，他的拥抱也充满了真诚。")]),t._v(" "),v("h3",{attrs:{id:"自尊"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#自尊"}},[t._v("#")]),t._v(" 自尊")]),t._v(" "),v("p",[t._v("我觉得坎贝尔是一个 自尊 极小的人，而大多数人终生对被困在「证明自己」的烦恼中。")]),t._v(" "),v("p",[t._v("为什么我们没有办法想坎贝尔那样去倾听？因为我们的 自尊 太大了。大到无法留出空间接受任何新的信息。这样，信息并不能透入到我们心中，而是碰到表面就弹走了。")]),t._v(" "),v("h2",{attrs:{id:"推荐阅读"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#推荐阅读"}},[t._v("#")]),t._v(" 推荐阅读")]),t._v(" "),v("ul",[v("li")])])}),[],!1,null,null,null);a.default=s.exports}}]);